{
    "name": "Argen Prop Venta Villa Ortuzar",
    "url": "https://www.argenprop.com/departamentos/venta",
    "page_query_parameter": "pagina-{number}",
    "pagination_schema": {
        "name": "Max Page",
        "baseSelector": "div.listing-container",
        "fields" : [
            {
                "name": "max_page",
                "selector": "ul.pagination li:nth-child(6)",
                "type": "text"
            }
        ]
    },
    "website_schema": {
        "name": "Houses",
        "baseSelector": "div.listing__items",
        "fields": [
            {
                "name": "houses",
                "selector": "div.listing__item",
                "type": "nested_list",
                "fields": [
                    {
                        "name": "external_id",
                        "selector": "a",
                        "type": "attribute",
                        "attribute": "data-item-card"
                    },
                    {
                        "name": "title",
                        "selector": "p.card__title--primary",
                        "type": "text"
                    },
                    {
                        "name": "address",
                        "selector": "p.card__address",
                        "type": "text"
                    },
                    {
                        "name": "photos",
                        "selector": "ul.card__photos",
                        "type": "nested_list",
                        "fields": [
                            {
                                "name": "image",
                                "type": "attribute",
                                "attribute": "data-src",
                                "selector": "li img"
                            },
                                                        {
                                "name": "image_2",
                                "type": "attribute",
                                "attribute": "src",
                                "selector": "li img"
                            }
                        ]
                    },
                    {
                        "name": "price",
                        "type": "attribute",
                        "selector": "div.card-contact-group span",
                        "attribute": "data-precio"
                    },
                    {
                        "name": "currency",
                        "selector": "div.card-contact-group span",
                        "attribute": "data-moneda",
                        "type": "attribute"
                    },
                    {
                        "name": "description",
                        "selector": "p.card__info",
                        "type": "text"
                    },
                    {
                        "name": "square_meters",
                        "type": "text",
                        "selector": "ul.card__main-features li:first-of-type"
                    }
                ]
            }
        ]
    }
}
